import React, { useContext, useState, createContext } from 'react';
import { Button, Switch, Grid, Typography, Paper, makeStyles } from '@material-ui/core';
import { SocketContext } from '../Context';

const VideoContext = createContext();

const useStyles = makeStyles((theme) => ({
  video: {
    width: '550px',
    [theme.breakpoints.down('xs')]: {
      width: '300px',
    },
  },
  gridContainer: {
    justifyContent: 'center',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column',
    },
  },
  paper: {
    padding: '10px',
    border: '2px solid black',
    margin: '10px',
  },
}));

const VideoPlayer = () => {
  const { name, callAccepted, myVideo, userVideo, callEnded, stream, call } = useContext(SocketContext);
  const [toggle1, setToggle1] = useState(false);
  const [toggle2, setToggle2] = useState(false);
  const classes = useStyles();
  let predictionVideo;
  if (toggle1 && !toggle2) {
    predictionVideo = myVideo;
  } else if (!toggle1 && toggle2) {
    predictionVideo = userVideo;
  }
  const handleToggle1 = () => {
    setToggle1(!toggle1);
  };
  const handleToggle2 = () => {
    setToggle2(!toggle2);
  };
  return (
    <VideoContext.Provider value={predictionVideo}>
      <Grid container className={classes.gridContainer}>
        {stream && (
          <Paper className={classes.paper}>
            <Grid item xs={12} md={6}>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <Typography variant="overline" gutterBottom>Start Prediction</Typography>
                <Switch
                  checked={toggle1}
                  onChange={handleToggle1}
                />
              </div>
              <Button
                variant="contained"
                style={{ backgroundColor: 'green', color: 'white' }}
              >
                Get Prediction
              </Button>
              <Typography variant="h5" gutterBottom>{name || '  Name'}</Typography>
              <video playsInline muted ref={myVideo} autoPlay className={classes.video} />
            </Grid>
          </Paper>
        )}
        {callAccepted && !callEnded && (
          <Paper className={classes.paper}>
            <Grid item xs={12} md={6}>
              <Typography variant="overline" gutterBottom>Start Prediction</Typography>
              <Switch
                checked={toggle2}
                onChange={handleToggle2}
                className={classes.switch}
              />
              <Button
                className={classes.getButton}
                variant="contained"
                style={{ backgroundColor: 'green', color: 'white' }}
              >
                Get Prediction
              </Button>
              <Typography variant="h5" gutterBottom>{call.name || 'Name'}</Typography>
              <video playsInline ref={userVideo} autoPlay className={classes.video} />
            </Grid>
          </Paper>
        )}
      </Grid>
    </VideoContext.Provider>
  );
};

export { VideoPlayer, VideoContext };
